def get_sum(a, b):
    # According to the puzzle,
    # if ever the two elements are the same,
    # just return that value
    if a == b:
        # We could return either a, or b, as they are the same
        return a

    # If the values are not the same,
    else:
        sm = min(a,b)
        lg = max(a,b)

        # Then we want the sum of the range of numbers.
        # We need to +1 because the upper argument of range()
        # is not inclusive.
        return sum(range(sm, lg+1)
